<?php

namespace Settings\ConfigBundle\Entity\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * TestProcedureRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class UnitOfMeasureRepository extends EntityRepository {
    
    public function getLatestUnits($limit = NULL) {
        $qb = $this->createQueryBuilder('uom')
                    ->select('uom')
                    ->addOrderBy('uom.updated', 'DESC');

        if (false === is_null($limit))
            $qb->setMaxResults($limit);

        return $qb->getQuery()
                ->getResult();
    }
}
